angular.module('ymusica').controller('AlbumSearch', ['$scope', function($scope) {

    $scope.albums = [];

    $scope.searchAlbums = function(term) {
        if(!term) {
            $scope.albums = [];
        } else {
            $scope.albums = albums.filter(function(album) {
                return album.name.toLowerCase().indexOf(term.toLowerCase()) !== -1;
            }).slice(0, 10);
        }

        return $scope.albums;
    };


    $scope.selectAlbum = function(album) {
        console.log('album selected!', album);
        return album.name;
    };

    $scope.imageSource = function(album) {
        return album.images['medium'];
    };

}]);


